VALUES (73, 11, E'dhinrichs', E'password', E'David Hinrichs  (Dave)', 1, E'Hydrogeologist', E'dhinrichs@newfields.com', E'303-378-1388', E'Mr. Hinrichs has 31 years of experience in the environmental field. As a project manager and lead consultant for NewFields, he represents several private sector mining companies involved in EPA-mandated CERCLA mine remedies and natural resource issues. His career milestones include designing and constructing a deep tunnel groundwater diversion that reduced the treatment demand at mining site near Vail, Colorado. Mr. Hinrichs received a B.S. in geology from Colorado State University. He is licensed \r\nin WY.', NULL, E'44d87e78.doc', NULL, NULL, NULL, NULL, E'2004-11-01', E'2004-11-01', E'303-294-9298', E'PG - WY', E'0563', 0, 1, NULL);

